package org.jimmutable.core.objects.common;

import org.jimmutable.core.utils.StringableTestingUtils;
import org.junit.Test;

import junit.framework.TestCase;

public class OpenGraphObjectIdTest extends TestCase
{
	private StringableTestingUtils<OpenGraphObjectId> tester = new StringableTestingUtils<OpenGraphObjectId>(new OpenGraphObjectId.MyConverter());

	@Test
	public void testValid()
	{
		assertTrue(tester.isValid("123456"));
	}

	@Test
	public void testInValid()
	{
		assertTrue(tester.isInvalid("12-3456"));
		assertTrue(tester.isInvalid("someids"));
		assertTrue(tester.isInvalid(""));
		assertTrue(tester.isInvalid("~`!@#$%^&*()_-+={[}]:;"));
		assertTrue(tester.isInvalid(""));
	}

}
